import 'package:flutter_test/flutter_test.dart';
import '../../../lib/features/product/data/models/product_model.dart';

void main() {
  const productModel = ProductModel(
    id: '1',
    name: 'Football',
    description: 'A high-quality leather football.',
    price: 49.99,
    imageUrl: 'data:image/jpeg;base64,',
  );

  final jsonMap = {
    'id': '1',
    'name': 'Football',
    'description': 'A high-quality leather football.',
    'price': 49.99,
    'imageUrl': 'data:image/jpeg;base64,',
  };

  group('ProductModel', () {
    test('fromJson returns a valid model', () {
      final model = ProductModel.fromJson(jsonMap);
      expect(model, isA<ProductModel>());
      expect(model.id, productModel.id);
      expect(model.name, productModel.name);
      expect(model.description, productModel.description);
      expect(model.price, productModel.price);
      expect(model.imageUrl, productModel.imageUrl);
    });

    test('toJson returns a valid map', () {
      final map = productModel.toJson();
      expect(map, jsonMap);
    });

    test('fromJson(toJson(model)) returns same model', () {
      final recreated = ProductModel.fromJson(productModel.toJson());
      expect(recreated, isA<ProductModel>());
      expect(recreated.id, productModel.id);
      expect(recreated.name, productModel.name);
      expect(recreated.description, productModel.description);
      expect(recreated.price, productModel.price);
      expect(recreated.imageUrl, productModel.imageUrl);
    });
  });
}
